mapper.update:
UPDATE invoices
SET total_price = #{totalPrice}
WHERE id = #{invoiceId}

update invoices
set is_active = false
where id = #{id}

mapper.select:
select *
from invoices
where id = #{invoiceId}
and recipient_tax_id = #{receiverTaxId}
and is_active = true
and status = 'PENDING'

select *
from invoices
where id = #{invoiceId}
and sender_tax_id = #{senderTaxId}
and is_active = true
and status = 'PENDING'
