mapper.update:
UPDATE invoices
SET total_price = #{totalPrice}
WHERE id = #{invoiceId}
  AND is_active = true

UPDATE invoices
SET is_active = false
WHERE id = #{id}

UPDATE invoices
SET updated_at = current_timestamp
WHERE id = #{invoiceId}

mapper.select:
SELECT * FROM invoices c
                  LEFT JOIN items i ON i.invoice_id = c.id AND i.is_active = true
WHERE c.id = #{invoiceId}
  AND c.recipient_tax_id = #{receiverTaxId}
  AND c.is_active = true

SELECT * FROM invoices c
                  LEFT JOIN items i ON i.invoice_id = c.id AND i.is_active = true
WHERE c.id = #{invoiceId}
  AND c.sender_tax_id = #{senderTaxId}
  AND c.is_active = true

SELECT * FROM invoices c
LEFT JOIN items i ON c.id = i.invoice_id AND i.is_active = true
WHERE c.is_active = true
AND c.sender_tax_id = #{senderTaxId}

    AND LOWER(c.status) = LOWER(#{filter.status})


    AND c.created_at >= #{filter.fromDate}


    AND c.created_at <= #{filter.toDate}


    AND EXTRACT(YEAR FROM c.created_at) = #{filter.year}


    AND c.invoice_number = #{filter.invoiceNumber}

ORDER BY c.created_at DESC

    LIMIT #{filter.limit} OFFSET #{filter.offset}
